    setLanguages(arr);
  }

  const setContributorData = (data) => {
    var arr = [];
    Object.keys(data).forEach(key => arr.push({x: data[key].login, y: data[key].contributions}));
    console.log(arr);
    setContributors(arr);
  }

  const setCommitData = (data) => {
    var arr = {};
    for(var commit in data){
      var commitDate = new Date(data[commit].committer.date.substring(0,10));
      if(arr[commitDate]){
          arr[commitDate]++;
      }
      else {
        arr[commitDate] = 1;
      }
      console.log(arr);
    }
