import React from 'react'
//
import { Chart } from 'react-charts'
import { useState, useEffect } from 'react';

function Line (){

  const [commits, setCommits] = useState([]);
  const [commit, set] = useState([]);

  useEffect(() => {
    getData();
    console.log(commit);
    setCommitsData(commit);
  }, [])

  const getData=()=>{
    fetch('commitfile.json'
    ,{
      headers : {
        'Content-Type': 'application/json',
        'Accept': 'application/json'
       }
    }
    )
      .then(function(response){
        return response.json();
      })
      .then(function(myJson) {
        set(myJson);
        console.log(myJson)
      });
  }

  const setCommitsData = (data) => {
    console.log(data);
    var obj = {};
    for(var commit in data){
      var commitDate = new Date(data[commit].commit.committer.date.substring(0,10));
      if(obj[commitDate]){
          obj[commitDate]++;
      }
      else {
        obj[commitDate] = 1;
      }
    }
    var arr = [];
    for(var node in obj){
        arr.push({x: new Date(node), y: obj[node]})
    }
    console.log(arr)
    var arr2 = [];
    var currentDate = arr[arr.length - 1].x;
    var max = arr[0].x;
    while(currentDate <= max){
      var count;
      if(obj[currentDate]){
        count = obj[currentDate];
      }
      else{
        count = 0;
      }
      arr2.push([new Date(currentDate),count]);
      currentDate.setDate(currentDate.getDate() + 1);
    }
    setCommits(arr2);
  }

  const series = React.useMemo(
    () => ({
      showPoints: false
    }),
    []
  )

  const commitData = React.useMemo(
    () => [
      {
        label: 'Commit Count',
        data: commits
      }
    ],
    [],
  )
  const lineAxes = React.useMemo(
    () => [
      { primary: true, type: 'utc', position: 'bottom' },
      { type: 'linear', position: 'left' }
    ],
    []
  )
  return (
    <Chart data={commitData} series={series} axes={lineAxes} tooltip />
  )
}

export default Line
