import { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import ReactDOM from 'react-dom';
import PieChar from './PieChart';



function App() {
  const [name, setName] = useState('name');
  const [languages, setLanguages] = useState('')

  useEffect(() => {
    fetch("https://api.github.com/repos/r-swords/LCAJava")
      .then(res =>res.json())
      .then(data =>{setData(data)});
    fetch("https://api.github.com/repos/r-swords/LCAJava/languages")
      .then(res => res.json())
      .then(data => setLanguages(data));
  }, [])

  const setData = ({name}) => {
    setName(name);
  }



  return (
    <div className="App">
      <h1>{name}</h1>
      {
      Object.keys(languages).map((key, i) => (
          <p key={i}>
            <span>Key Name: {key} </span>
            <span>Value: {languages[key]}</span>
          </p>
        ))
      }
      <PieChart data={languages} outerRadius='200' innerRadius='100' />
    </div>
  );
}

export default App;
